$(function() {
    isMobile.phone ? $(".infoContent").css({
        height: $(window).height() + 80 + "px"
    }) : ($(".module > div").css({
        "margin-top": "-" + $(".module > div").height() / 2 + "px"
    }), $(".infoContent").css({
        "margin-top": -($(".infoContent").height() / 2) + "px"
    }))
}), $(window).resize(function() {
    $(".infoContent").css(isMobile.phone ? {
        height: $(window).height() + 80 + "px"
    } : {
        "margin-top": -($(".infoContent").height() / 2) + "px"
    })
}), $(window).on("load", function() {
    $(".module.info").css({
        opacity: "1"
    })
});